Ext.define('Linda_db_admin.view.winSelectPic', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.form.Panel',
        'Ext.form.field.File',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    autoShow: true,
    height: 165,
    id: 'mywin1_id',
    itemId: 'mywin1',
    width: 457,
    layout: 'fit',
    title: '选择图片',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    height: 166,
                    bodyPadding: 10,
                    url: 'plant/pics_upload',
                    items: [
                        {
                            xtype: 'filefield',
                            anchor: '100%',
                            formBind: false,
                            fieldLabel: '图片名',
                            labelWidth: 50,
                            name: 'upfile',
                            allowBlank: false,
                            emptyText: '请选择图片文件',
                            buttonText: '选择图片...'
                        }
                    ],
                    dockedItems: [
                        {
                            xtype: 'toolbar',
                            dock: 'bottom',
                            items: [
                                {
                                    xtype: 'button',
                                    handler: function(button, e) {
                                        theForm = Ext.ComponentQuery.query('form[title="种编辑表单"]')[0];
                                        theRecord = theForm.getRecord();
                                        pic_path = theRecord.get('pics');
                                        zhongId = theRecord.get('zhong_id');

                                        if (pic_path === "" || typeof(pic_path) == 'undefined'){
                                            Ext.MessageBox.alert('错误提示','没有定义该种植物的图片目录，请检查！');
                                            return false;
                                        } else {

                                            var form = this.up('form').getForm();

                                            if(form.isValid()){

                                                form.submit({
                                                    url: 'plant/pics_upload',
                                                    params: {                         //form.commit()附加参数，upload图片的同时给定目标目录.
                                                        picpath: pic_path
                                                    },
                                                    waitMsg: '正在上传图片...',
                                                    success: function(form,action) {
                                                        Ext.Msg.alert('成功', action.result.message);
                                                        Ext.getStore('PicStore').load({
                                                            params: {
                                                                id: theRecord.get(zhongId)
                                                            }
                                                        });
                                                    },
                                                    failure: function(form,action) {
                                                        Ext.Msg.alert('失败', action.result.message);

                                                    }
                                                });
                                            }//if

                                        }







                                    },
                                    text: '上传图片'
                                }
                            ]
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    }

});